  const params = new URLSearchParams(window.location.search);
let currentQuestion = 0;
let score = 0;
let attemptedQuestions = 0;
let timer;
let questionsArray = [];

const quizConfig = {
  username: params.get('username') || 'Anonymous',
  topic: params.get('topic') || 'physics',
  level: params.get('level') || 'easy',
  timeLimit: parseInt(params.get('time')) || 30
};

function loadQuizState() {
  const savedState = sessionStorage.getItem('quizState');
  if (savedState) {
    const state = JSON.parse(savedState);
    currentQuestion = state.currentQuestion;
    score = state.score;
    attemptedQuestions = state.attemptedQuestions;
    if (state.questionsArray) {
      questionsArray = state.questionsArray;
      return;
    }
  }
  questionsArray = [...questions[quizConfig.topic][quizConfig.level]];
  shuffleArray(questionsArray);
  currentQuestion = 0;
  score = 0;
  attemptedQuestions = 0;
}

function saveQuizState() {
  const state = { 
    currentQuestion, 
    score, 
    attemptedQuestions,
    questionsArray 
  };
  sessionStorage.setItem('quizState', JSON.stringify(state));
}

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

function initializeQuiz() {
  loadQuizState();
  updateProgress();
  loadQuestion();
}

function startTimer() {
  let timeLeft = quizConfig.timeLimit;
  updateTimer(timeLeft);
  timer = setInterval(() => {
    timeLeft--;
    updateTimer(timeLeft);
    if (timeLeft <= 0) {
      clearInterval(timer);
      handleTimeout();
    }
  }, 1000);
}

// Updated timer display with leading zero formatting
function updateTimer(seconds) {
  const formattedSeconds = seconds < 10 ? '0' + seconds : seconds;
  document.getElementById('timer').textContent = formattedSeconds + 's';
}

function handleTimeout() {
  clearInterval(timer);
  attemptedQuestions++;
  revealCorrectAnswer();
  disableOptions();
  showNextButton();
  saveQuizState();
}

function updateProgress() {
  const total = questionsArray.length;
  document.getElementById('progressText').textContent = 
    `Question ${currentQuestion + 1} of ${total}`;
  const percentage = Math.round(((currentQuestion + 1) / total) * 100);
  document.getElementById('progressFill').style.width = percentage + '%';
}

function loadQuestion() {
  clearInterval(timer);
  const question = questionsArray[currentQuestion];
  let optionsHTML = '';

  if (question.type === 'truefalse') {
    optionsHTML = `
      <div class="option" role="button" onclick="checkAnswer('true')">True</div>
      <div class="option" role="button" onclick="checkAnswer('false')">False</div>`;
  } else {
    optionsHTML = question.options.map((opt, i) => `
      <div class="option" role="button" onclick="checkAnswer(${i})">${opt}</div>`
    ).join('');
  }

  document.getElementById('quizArea').innerHTML = `
    <div class="question fade-in">${currentQuestion + 1}. ${question.question}</div>
    <div class="options fade-in">${optionsHTML}</div>`;
  
  document.getElementById('topicLevel').textContent = 
    `${quizConfig.topic.toUpperCase()} • ${quizConfig.level.toUpperCase()}`;
  
  document.querySelector('.next-btn:not(.give-up-btn)').style.display = 'none';
  saveQuizState();
  startTimer();
}

function checkAnswer(answer) {
  clearInterval(timer);
  attemptedQuestions++;
  const question = questionsArray[currentQuestion];
  let isCorrect = false;

  if (question.type === 'mcq') {
    isCorrect = answer === question.correct;
  } else {
    isCorrect = (answer.toLowerCase() === question.correct);
  }

  if (isCorrect) {
    score++;
    document.getElementById('correctSound').play();
  } else {
    document.getElementById('wrongSound').play();
  }

  highlightAnswers(question, answer);
  disableOptions();
  showNextButton();
  saveQuizState();
}

function highlightAnswers(question, selected) {
  document.querySelectorAll('.option').forEach((opt, index) => {
    if (question.type === 'mcq') {
      if (index === question.correct) opt.classList.add('correct');
      if (index === selected && index !== question.correct) opt.classList.add('wrong');
    } else {
      if (opt.textContent.toLowerCase() === question.correct) opt.classList.add('correct');
      else if (selected) opt.classList.add('wrong');
    }
  });
}

// Added function to reveal the correct answer when time runs out
function revealCorrectAnswer() {
  const question = questionsArray[currentQuestion];
  document.querySelectorAll('.option').forEach((opt, index) => {
    if (question.type === 'mcq') {
      if (index === question.correct) opt.classList.add('correct');
    } else {
      if (opt.textContent.toLowerCase() === question.correct) opt.classList.add('correct');
    }
  });
}

function disableOptions() {
  document.querySelectorAll('.option').forEach(opt => opt.classList.add('disabled'));
}

function showNextButton() {
  document.querySelector('.next-btn:not(.give-up-btn)').style.display = 'block';
}

function loadNextQuestion() {
  currentQuestion++;
  updateProgress(); // Update progress bar when moving to next question
  if (currentQuestion < questionsArray.length) {
    loadQuestion();
  } else {
    showFinalResults();
  }
}

function handleGiveUp() {
  clearInterval(timer);
  showFinalResults();
}

function showFinalResults() {
  clearInterval(timer);
  sessionStorage.removeItem('quizState');
  const totalAttempted = attemptedQuestions;
  const percentage = totalAttempted > 0 ? Math.round((score / totalAttempted) * 100) : 0;

  const leaderboardKey = `${quizConfig.topic}_${quizConfig.level}_leaderboard`;
  let leaderboard = JSON.parse(localStorage.getItem(leaderboardKey)) || [];
  
  leaderboard.push({
    username: quizConfig.username,
    score: percentage,
    timestamp: Date.now()
  });

  leaderboard.sort((a, b) => {
    if (b.score === a.score) return b.timestamp - a.timestamp;
    return b.score - a.score;
  });
  leaderboard = leaderboard.slice(0, 5);
  localStorage.setItem(leaderboardKey, JSON.stringify(leaderboard));

  const highScore = leaderboard[0] || { score: 0, username: '' };

  document.getElementById('quizArea').innerHTML = `
    <div class="completed-message fade-in">
      <div class="celebrate-emoji">${getCelebrationEmoji(percentage)}</div>
      <div class="score-circle">
        <svg viewBox="0 0 100 100">
          <circle cx="50" cy="50" r="45" fill="none" stroke="var(--background)" stroke-width="8"/>
          <circle class="circle-progress" cx="50" cy="50" r="45" fill="none" stroke-width="8" 
                  stroke-dasharray="${283 * percentage/100} 283"/>
        </svg>
        <div class="score-text">${percentage}%</div>
      </div>
      <h2>${getMotivationalTitle(percentage)}</h2>
      <p class="motivation-text">${getMotivationalMessage(percentage)}</p>
      <p style="margin: 2rem 0; font-size: 1.2rem;">
        ${quizConfig.username} got ${score} out of ${totalAttempted} correct!
      </p>
      <p style="font-size: 1rem;">
        🏆 High Score: ${highScore.score}% by ${highScore.username}
      </p>
      <button onclick="window.location.href='quiz.html'" 
              class="next-btn play-again-btn">
        Play Again 🔄
      </button>
    </div>`;
  
  document.querySelector('.action-buttons').style.display = 'none';
}

function getCelebrationEmoji(percentage) {
  if (percentage >= 90) return '🏆🎉';
  if (percentage >= 70) return '🎉👍';
  if (percentage >= 50) return '🙌';
  return '💪';
}

function getMotivationalTitle(percentage) {
  if (percentage >= 90) return 'Quiz Master!';
  if (percentage >= 70) return 'Great Job!';
  if (percentage >= 50) return 'Good Effort!';
  return 'Keep Trying!';
}

function getMotivationalMessage(percentage) {
  const messages = {
    high: [
      "You're a quiz superstar! 🌟",
      "Incredible performance! 🚀",
      "Perfect score territory! 💯"
    ],
    medium: [
      "Almost there! Keep going! 🔥",
      "You've got this! 💪",
      "Great progress! 📈"
    ],
    low: [
      "Every master was once a beginner! 🌱",
      "Practice makes perfect! 📚",
      "Next time you'll crush it! 💥"
    ]
  };
  
  if (percentage >= 80) return messages.high[Math.floor(Math.random() * messages.high.length)];
  if (percentage >= 50) return messages.medium[Math.floor(Math.random() * messages.medium.length)];
  return messages.low[Math.floor(Math.random() * messages.low.length)];
}

initializeQuiz();